INSERT INTO Champions (idChampion, nomChampion, title, blurb, attack, defense, magic, difficulty, idType) VALUES (53, 'Kalista', 'Lance de la vengeance', 'Spectre courroucé des Îles obscures, Kalista est l''esprit immortel de la vengeance, un cauchemar en armure que l''on invoque pour pourchasser les traîtres et les menteurs. Les victimes trahies peuvent réclamer vengeance, mais Kalista ne répond qu''à ceux...', 8, 2, 4, 7, NULL);
